jQuery.sap.declare("ui.s2p.mm.purcontract.approve.Component");
jQuery.sap.require("ui.s2p.mm.purcontract.approve.Configuration");
jQuery.sap.require("sap.ca.scfld.md.ComponentBase");


// new Component
sap.ca.scfld.md.ComponentBase.extend("ui.s2p.mm.purcontract.approve.Component", {

	metadata: sap.ca.scfld.md.ComponentBase.createMetaData("MD", {
		"name": "Purchase Contract Approve",
		"version": "1.0.0",
		"library": "ui.s2p.mm.purcontract.approve",
		"includes": [],
		"dependencies": {
			"libs": ["sap.m", "sap.me"],
			"components": []
		},
		"config": {
			"resourceBundle": "i18n/i18n.properties",
			"titleResource": "SHELL_TITLE",
			"icon": "sap-icon://expense-report",
			"favIcon": "./resources/sap/ca/ui/themes/base/img/favicon/Approve_Purchase_Contracts.ico",
			"homeScreenIconPhone": "./resources/sap/ca/ui/themes/base/img/launchicon/F0400_Approve_Purchase_Contracts/57_iPhone_Desktop_Launch.png",
			"homeScreenIconPhone@2": "./resources/sap/ca/ui/themes/base/img/launchicon/F0400_Approve_Purchase_Contracts/114_iPhone-Retina_Web_Clip.png",
			"homeScreenIconTablet": "./resources/sap/ca/ui/themes/base/img/launchicon/F0400_Approve_Purchase_Contracts/72_iPad_Desktop_Launch.png",
			"homeScreenIconTablet@2": "./resources/sap/ca/ui/themes/base/img/launchicon/F0400_Approve_Purchase_Contracts/144_iPad_Retina_Web_Clip.png"
		},

		// Navigation
		masterPageRoutes: {
			"master": {
				"pattern": ":scenarioId:",
				"view": "ui.s2p.mm.purcontract.approve.view.S2"
			}
		},
		detailPageRoutes: {
			"detail": {
				"pattern": "HeaderDetails/{contextPath}",
				"view": "ui.s2p.mm.purcontract.approve.view.S3"
			},
			"itemDetails": {
				"pattern": "itemDetails/{SAP__Origin}/{WorkitemID}/{PoNumber}/{ItemNumber}",
				"view": "ui.s2p.mm.purcontract.approve.view.ItemDetails"
			},
			"ServiceLineDetails": {
				"pattern": "ServiceLineDetails/{SAP__Origin}/{WorkitemID}/{PoNumber}/{ItemNumber}/{ServiceLineNumber}",
				"view": "ui.s2p.mm.purcontract.approve.view.ItemServiceLine"
			},
			"noData": {
				"pattern": "noData",
				"view": "empty"
			}
		}
	}),

	/**
	 * Initialize the application
	 *
	 * @returns {sap.ui.core.Control} the content
	 */
	createContent: function() {

		var oViewData = {
			component: this
		},
			oView = sap.ui.view({
				viewName: "ui.s2p.mm.purcontract.approve.Main",
				type: sap.ui.core.mvc.ViewType.XML,
				viewData: oViewData
			}),
			sPrefix = oView.getId() + "--",
			oEventBus = sap.ui.getCore().getEventBus();

		this.oEventBus = {
			publish: function(channelId, eventId, data) {
				channelId = sPrefix + channelId;
				oEventBus.publish(channelId, eventId, data);
			},
			subscribe: function(channelId, eventId, data, oListener) {
				channelId = sPrefix + channelId;
				oEventBus.subscribe(channelId, eventId, data, oListener);
			},
			unsubscribe: function(channelId, eventId, data, oListener) {
				channelId = sPrefix + channelId;
				oEventBus.unsubscribe(channelId, eventId, data, oListener);
			}
		};

		return oView;
	}

});

