	 * @private 
	 */
	sap.ui.core.util.MockServer.prototype._createKeysString = function(aKeys, oEntry) {
		// creates the key string for an entity
		var sKeys = "";
		if (oEntry) {
			jQuery.each(aKeys, function(iIndex, sKey) {
				if (sKeys) {
					sKeys += ",";
				}
