		formatNumber: function (number, options) {
			var numValue  = ui.s2p.mm.purcontract.approve.util.Conversions.toNumeric(number);
			if (!isFinite(numValue)) {
				return "";
			}
			var formatOptions = ui.s2p.mm.purcontract.approve.util.Conversions.getFormatOptions(options);
			var numberFormatter = sap.ui.core.format.NumberFormat.getFloatInstance({}, formatOptions.locale);
			if (ui.s2p.mm.purcontract.approve.util.Conversions.hasRounding(options)) {
				numValue = ui.s2p.mm.purcontract.approve.util.Conversions.roundNumber(numValue, formatOptions);
				return numberFormatter.format(numValue);
			} else {
				return numberFormatter.format(number);
			}
		},
		

		roundNumber: function(number, options) {
			return sap.ca.ui.model.format.FormatHelper.roundNumber(number, options);
		},


		hasRounding: function(options) {
			var rounding;
			if (options !== undefined) {
				if (typeof options === "number") {
					rounding = true;
				} else {
					if (options.lazy) {
						rounding = (options.lazyDecimals !== undefined) || (options.lazyRounding !== undefined);
					} else {
						rounding = (options.decimals !== undefined) || (options.rounding !== undefined);
					}
				}
			} else {
				rounding = false;
			}
			return rounding;
		},


		getFormatOptions: function(options, lazy) {
			var formatOptions = {};
			var t = typeof options;
			switch (t) {
			case "number":
				if (lazy) {
					formatOptions.lazyDecimals = options;
				} else {
					formatOptions.decimals = options;
				}
				break;
			case "object":
				if (typeof options.locale === "string") {
					formatOptions.locale = new sap.ui.core.Locale(options.locale);
				} else {
					formatOptions.locale = options.locale;
				}
				formatOptions.decimals = options.decimals;
		        formatOptions.rounding = options.rounding;
		        formatOptions.lazy = options.lazy;
		        formatOptions.lazyDecimals = options.lazyDecimals;
		        formatOptions.lazyRounding = options.lazyRounding;
		        break;
			}
			if (lazy !== undefined) {
				formatOptions.lazy = lazy;
			}
			if (!formatOptions.locale) {
				if (sap.ui.getCore().getConfiguration().getLanguage() == "ZH") {
					formatOptions.locale = new sap.ui.core.Locale("zh_CN");
				} else {
					formatOptions.locale = sap.ui.getCore().getConfiguration().getFormatSettings().getFormatLocale();
				}
			}
			return formatOptions;
		},

		
		toNumeric: function(obj) {
			return sap.ca.ui.model.format.FormatHelper.toNumeric(obj);
		},


		stringToNumeric: function(str) {
			return sap.ca.ui.model.format.FormatHelper.stringToNumeric(str);
		},


		formatDaysAgo: function(oDate) {
			if (oDate == null || oDate == "") {
				return "";
			} else {
				var formatter = sap.ca.ui.model.format.DateFormat.getDateInstance({style : "daysAgo"});
				var oFormatDate = formatter.format(oDate);
				return oFormatDate;
			}
		},
		
		pricingScaleFormatter: function( sScaleType, sScaleFrom, sScaleTo, sScaleUnit) {
			var oBundle = sap.ca.scfld.md.app.Application.getImpl().getResourceBundle();
		    if (oBundle === undefined) {
		        return '';
		    }
		   
		    var sComplexScale = (oBundle !== undefined) ? oBundle.getText("view.PurchaseContract.complexScale") : "";
		    var sResult ="";
		    if( sScaleType==""){
		        return "";
		    }
		    else if (sScaleType=="C"){
		        if(sScaleTo==""){
		            sResult=sScaleFrom+ ' '+ sScaleUnit;
		            return oBundle.getText("view.PurchaseContract.over", [ sResult ]);
		        }
		        else{
		            sResult = sScaleFrom+ ' - '+ sScaleTo+ ' '+ sScaleUnit;
		            return sResult;
		        }
		    }
		    else{
		         return sComplexScale;
		    }
		},

		pricingValidityDateFormatter: function(sStartDate, sEndDate) {
			var oBundle = sap.ca.scfld.md.app.Application.getImpl().getResourceBundle();
			if (oBundle === undefined) {
		        return '';
		    }
			if(sStartDate ==null ||sStartDate ==""){
		        return'';
		    }
			sStartDate=ui.s2p.mm.purcontract.approve.util.Conversions.fConvert(sStartDate);
			sEndDate=ui.s2p.mm.purcontract.approve.util.Conversions.fConvert(sEndDate);
			var dateOfToday=new Date();
			if(!sEndDate || !sStartDate){
			     return'';
			}
			if(sEndDate.getFullYear()>2998){
			     var oDate1 = ui.s2p.mm.purcontract.approve.util.Conversions.formatMediumDate(sStartDate);
			     return oBundle.getText("view.PurchaseContract.since",["\n"+oDate1]);
			}
			else if (sStartDate<dateOfToday) {
			     var oDate2 = ui.s2p.mm.purcontract.approve.util.Conversions.formatMediumDate(sEndDate);
			     return oBundle.getText("view.PurchaseContract.until",["\n"+oDate2]);
		    }
			else{
			     var oDate1 = ui.s2p.mm.purcontract.approve.util.Conversions.formatMediumDate(sStartDate);
			     var oDate2 = ui.s2p.mm.purcontract.approve.util.Conversions.formatMediumDate(sEndDate);
			     return oDate1 + ' -\n' + oDate2;
			}
	    }
		
