sap.ca.ui.model.format.NumberFormat.oValueInfo = {
    oDefaultFormatOptions: {
        style: "standard"
    }
};


/**
 * @see sap.ui.core.format.NumberFormat.getInstance
 */
sap.ca.ui.model.format.NumberFormat.getInstance = function(oFormatOptions, oLocale) {
