sap.ca.scfld.md.controller.BaseDetailController.extend("ui.s2p.mm.purcontract.approve.view.S3", {

	onInit: function() {

		var that = this;

		// Get connection manager/resource bundle
		this.resourceBundle = this.oApplicationFacade.getResourceBundle();
		this.oDataModel = this.oApplicationFacade.getODataModel();
		this.oRouter.attachRouteMatched(this._handleRouteMatched, this);
		this.oHeaderFooterOptions = {
			oPositiveAction: {
				sI18nBtnTxt: that.resourceBundle.getText("XBUT_APPROVE"),
				onBtnPressed: jQuery.proxy(that.handleApprove, that)
			},
			buttonList: [{
				sId: "btn_Forward",
				sI18nBtnTxt: "XBUT_FORWARD",
				onBtnPressed: jQuery.proxy(this.handleForward, that)
			}],
			onBack: jQuery.proxy(function() {
				//Check if a navigation to master is the previous entry in the history
				var sDir = sap.ui.core.routing.History.getInstance().getDirection(this.oRouter.getURL("master"));
				if (sDir === "Backwards") {
					window.history.go(-1);
				} else {
					//we came from somewhere else - create the master view
					this.oRouter.navTo("master");
				}
			}, this)
		};
		this.setHeaderFooterOptions(this.oHeaderFooterOptions);
		this.oView = this.getView();
		this.oTabBar = this.oView.byId("TabContainer");
		if (this.oTabBar.getSelectedKey() != "contentInfo") {
			this.oTabBar.setSelectedKey("contentInfo");
		}
	},

	_handleRouteMatched: function(oEvent) {
		if (oEvent.getParameter("name") === "detail") {
			var sMasterContextPath = oEvent.getParameter("arguments").contextPath;
			sMasterContextPath = sMasterContextPath.replace("WorkflowTaskCollection", "/WorkflowTaskCollection");
			var oViewContext = new sap.ui.model.Context(this.oView.getModel(), sMasterContextPath);
			var sDetailContextPath = oEvent.getParameter("arguments").contextPath + "/HeaderDetails";
			sDetailContextPath = sDetailContextPath.replace("WorkflowTaskCollection", "/WorkflowTaskCollection");

			this.getView().bindElement(sDetailContextPath, {
				expand: 'ItemDetails,Notes,Attachments'
			});

			if (this.oTabBar.getSelectedKey() != "contentInfo") {
				this.oTabBar.setSelectedKey("contentInfo");
			}
		}
	},

	onLineItemPress: function(oEvent) {
		var bc = oEvent.oSource.getBindingContext().getPath();
		var oModel = this.getView().getModel();

		this.oRouter.navTo("itemDetails", {
			SAP__Origin: this.oView.getBindingContext().getProperty("SAP__Origin"),
			WorkitemID: this.oView.getBindingContext().getProperty("WorkitemID"),
			PoNumber: this.oView.getBindingContext().getProperty("PoNumber"),
			ItemNumber: oModel.getProperty(bc).ItemNumber //this.oView.getBindingContext().getProperty("ItemNumber")

		}, false);
	},

	handleApprove: function() {

		var oDataObj = this.oView.getModel().getProperty(this.oView.getBindingContext().getPath()),
			bApprove = true,
			fnClose = function(oResult) {
				this.handleApproveRejectExecute(oResult, bApprove, oDataObj);
			},
			CreatedByID = this.oView.getBindingContext().getProperty("CreatedByID"),
			sApproveText = this.resourceBundle.getText("dialog.question.approve", [CreatedByID]);

		// open the confirmation dialog
		sap.ca.ui.dialog.confirmation.open({
			question: sApproveText,
			showNote: true,
			title: this.resourceBundle.getText("XTIT_APPROVAL"),
			confirmButtonLabel: this.resourceBundle.getText("XBUT_APPROVE")
		}, jQuery.proxy(fnClose, this));
	},

	handleApproveRejectExecute: function(oResult, bApprove, oDataObj) {

		if (oResult.isConfirmed) {
			var oEntry = {}, DecisionKey, sURL;
			if (oResult.sNote) {
				oEntry.Comment = encodeURIComponent(oResult.sNote);
			} else {
				oEntry.Comment = "";
			}

			if (bApprove) {
				DecisionKey = "0001";
				this.sTextKey = "dialog.success.approve";
			} else {
				DecisionKey = "0002";
				this.sTextKey = "dialog.success.reject";
			}
			oEntry.DecisionKey = DecisionKey;
			oEntry.SAP__Origin = oDataObj.SAP__Origin;

			sURL = "ApplyDecision?SAP__Origin='" + oDataObj.SAP__Origin + "'&WorkitemID='" + oDataObj.WorkitemID + "'&DecisionKey='" + DecisionKey + "'&Comment='" + oEntry.Comment + "'";

			this.oDataModel.setRefreshAfterChange(false);
			this.oDataModel.create(sURL, oEntry, null, jQuery.proxy(this._handleApproveRejectSuccess, this), jQuery.proxy(this._handleApproveRejectForwardFail, this));
		}
	},

	_handleApproveRejectSuccess: function() {
		var sComponentId = sap.ui.core.Component.getOwnerIdFor(this.oView),
			oComponent = sap.ui.component(sComponentId);

		oComponent.oEventBus.publish("ui.s2p.mm.purcontract.approve", "pcApproveRejectForward");

		this.oDataModel.setRefreshAfterChange(true);
		this.oDataModel.refresh(true);

		sap.ca.ui.message.showMessageToast(this.resourceBundle.getText(this.sTextKey));
	},

	_handleApproveRejectForwardFail: function(oError) {
		this.oDataModel.setRefreshAfterChange(true);
		if (this.oDataModel.hasPendingChanges()) {
			this.oDataModel.refresh(true);
		}

		sap.ca.ui.message.showMessageBox({
			type: sap.ca.ui.message.Type.ERROR,
			message: oError.message,
			details: oError.response.body
		});
	},

	onAttachment: function(oEvent) {
		var oContext = oEvent.getSource().getBindingContext();
		var sMediaSrc = oContext.getProperty().__metadata.media_src;
		sap.m.URLHelper.redirect(sMediaSrc, true);
	},

	handleForward: function(oEvent) {
		var that = this;
		var oDataObj = this.getView().getModel().getProperty(
			this.getView().getBindingContext().getPath());
		var sOrigin = oDataObj.SAP__Origin;
		var sWorkitemID = oDataObj.WorkitemID;
		var fnStartSearch = function(sQeury) {
			that.oDataModel.read("/ForwardingAgentCollection", null, ["$filter=SearchForText eq '" + sQeury + "' and SAP__Origin eq '" + sOrigin + "'"], true, function(oData,
				oResponse) {
				sap.ca.ui.dialog.forwarding.setFoundAgents(oData.results);
			}, jQuery.proxy(that.onRequestFailed, this));
		};
		var fnClose = function(oResult) {
			if (oResult && oResult.bConfirmed) {
				var oSelectedAgent = oResult.oAgentToBeForwarded;
				var sText = that.resourceBundle.getText("dialog.success.forward", [oSelectedAgent.FullName]);
				jQuery.sap.log.info(oSelectedAgent.UserId + " " + oResult.sNote);
				var oEntry = {};
				if (oResult.sNote) {
					oEntry.Comment = encodeURIComponent(oResult.sNote);
				} else {
					oEntry.Comment = "";
				}

				that.oDataModel.setRefreshAfterChange(false);
				that.oDataModel.create("Forward?SAP__Origin='" + sOrigin + "'&WorkitemID='" + sWorkitemID + "'&NewApprover='" + oSelectedAgent.UserId + "'&Comment='" + oEntry.Comment + "'",
					oEntry, null, function() {
						var sComponentId = sap.ui.core.Component.getOwnerIdFor(that.oView),
							oComponent = sap.ui.component(sComponentId);
						oComponent.oEventBus.publish("ui.s2p.mm.purcontract.approve", "pcApproveRejectForward");
						that.oDataModel.setRefreshAfterChange(true);
						that.oDataModel.refresh(true);
						sap.ca.ui.message.showMessageBox({
							type: sap.ca.ui.message.Type.SUCCESS,
							message: sText,
							details: sText
						});
					}, jQuery.proxy(that._handleApproveRejectForwardFail, that));
			}
		};
		sap.ca.ui.dialog.forwarding.start(fnStartSearch, fnClose);
	},

	onNamePress: function(oEvent) {
		this.openEmployeeLaunch(oEvent, "CreatedByID");
	},

	onForwardedPress: function(oEvent) {
		this.openEmployeeLaunch(oEvent, "ForwardedByID");
	},

	onSubstitutingPress: function(oEvent) {
		this.openEmployeeLaunch(oEvent, "SubstitutingForID");
	},

	onAgentPress: function(oEvent) {
		this.openEmployeeLaunch(oEvent, "CreatedByID");
	},

	onNoteSenderPress: function(oEvent) {
		this.openEmployeeLaunch(oEvent, "CreatedByID");
	},

	openEmployeeLaunch: function(oEvent, sRef) {

		var oControl = oEvent.getSource();
		var sTitle = this.resourceBundle.getText("BussinessCard.Employee");

		// Open employee type business card
		var onRequestSuccess = function(oData) {

			var data = oData.results[0],
				oEmpConfig = {
					title: sTitle,
					name: data.FullName,
					department: data.Department,
					contactmobile: data.MobilePhone,
					contactphone: data.WorkPhone,
					contactemail: data.EMail,
					companyname: data.CompanyName,
					companyaddress: data.AddressString
				},
				oEmployeeLaunch = new sap.ca.ui.quickoverview.EmployeeLaunch(oEmpConfig);
			oEmployeeLaunch.openBy(oControl);
		};

		var sOrigin = oEvent.getSource().getBindingContext().getProperty("SAP__Origin");
		var sUser = oEvent.getSource().getBindingContext().getProperty(sRef);

		this.oDataModel.read("UserDetailsCollection", null, ["$filter=UserID eq '" + sUser + "' and SAP__Origin eq '" + sOrigin + "'"], true,
			jQuery.proxy(onRequestSuccess, this),
			jQuery.proxy(this._onRequestFailed, this));
	},

	onVendorPress: function(oEvent) {
		this.onCompanyLaunch(oEvent, "SupplierID");
	},

	onCompanyLaunch: function(oEvent, sRef) {
		var sTitle = this.oApplicationFacade.getResourceBundle().getText("BussinessCard.Supplier");
		var sSupplierId = this.oApplicationFacade.getODataModel().getProperty("SupplierID", this.getView().getBindingContext());

		var oControl = oEvent.getParameters().domRef;
		var sOrigin = this.oView.getBindingContext().getProperty("SAP__Origin");
		var oApplicationFacade = this.oApplicationFacade;
		var sSupplierDetailsCollection = "SupplierDetailCollection(SupplierID='" + sSupplierId + "',SAP__Origin='" + sOrigin + "')";
		var aParam = ["$expand=SupplierContacts"];
		sap.ca.ui.utils.busydialog.requireBusyDialog();
		oApplicationFacade.getODataModel().read(sSupplierDetailsCollection, null, aParam, true, function(oData, response) {
			sap.ca.ui.utils.busydialog.releaseBusyDialog();
			var hasContacts = (oData.SupplierContacts && (oData.SupplierContacts.length > 0));
			var oSBCData = {
				title: sTitle,
				//						imgurl : "img/home/Company_48x48.jpg" ,
				companyname: oData.SupplierName,
				companyphone: oData.WorkPhone,
				companyaddress: oData.AddressString,
				maincontactname: hasContacts ? oData.SupplierContacts[0].ContactName : "",
				maincontactmobile: hasContacts ? oData.SupplierContacts[0].MobilePhone : oData.WorkPhone,
				maincontactphone: hasContacts ? oData.SupplierContacts[0].WorkPhone : oData.WorkPhone,
				maincontactemail: hasContacts ? oData.SupplierContacts[0].oData.EMail : oData.EMail,
			};
			var oSupplierBusinessCard = new sap.ca.ui.quickoverview.CompanyLaunch(oSBCData);
			oSupplierBusinessCard.openBy(oControl);
		}, function(oError) {
			sap.ca.ui.utils.busydialog.releaseBusyDialog();
		});
	},

	_onRequestFailed: function(oError) {
		sap.ca.ui.message.showMessageBox({
			type: sap.ca.ui.message.Type.ERROR,
			message: oError.message,
			details: oError.response.body
		});
	},

	isMainScreen: function() {
		return true;
	}
});

